SELECT COUNT(station_id) AS stationen, pollutant_name_de, aggregation_name_de FROM (SELECT * FROM (SELECT station_id, pollutant_name_de, aggregation_name_de, COUNT(`year`) AS years FROM open_data.bafu_daten WHERE `year` >= 1993 GROUP BY station_id, pollutant_name_de, aggregation_name_de) AS res WHERE res.years = 23 ORDER BY pollutant_name_de) AS t2 GROUP BY pollutant_name_de, aggregation_name_de;